CREATE TABLE "django_migrations" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "app" VARCHAR(255) NOT NULL, "name" VARCHAR(255) NOT NULL, "applied" DATETIME NOT NULL)
CREATE TABLE sqlite_sequence(name,seq)
CREATE TABLE "auth_group_permissions" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "group_id" INT NOT NULL REFERENCES "auth_group" ("id") , "permission_id" INT NOT NULL REFERENCES "auth_permission" ("id") )
CREATE TABLE "auth_user_groups" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "user_id" INT NOT NULL REFERENCES "auth_user" ("id") , "group_id" INT NOT NULL REFERENCES "auth_group" ("id") )
CREATE TABLE "auth_user_user_permissions" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "user_id" INT NOT NULL REFERENCES "auth_user" ("id") , "permission_id" INT NOT NULL REFERENCES "auth_permission" ("id") )
CREATE TABLE "django_admin_log" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "object_id" TEXT NULL, "object_repr" VARCHAR(200) NOT NULL, "action_flag" smallint unsigned NOT NULL CHECK ("action_flag" >= 0), "change_message" TEXT NOT NULL, "content_type_id" INT NULL REFERENCES "django_content_type" ("id") , "user_id" INT NOT NULL REFERENCES "auth_user" ("id") , "action_time" DATETIME NOT NULL)
CREATE TABLE "django_content_type" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "app_label" VARCHAR(100) NOT NULL, "model" VARCHAR(100) NOT NULL)
CREATE TABLE "auth_permission" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "content_type_id" INT NOT NULL REFERENCES "django_content_type" ("id") , "codename" VARCHAR(100) NOT NULL, "name" VARCHAR(255) NOT NULL)
CREATE TABLE "auth_group" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "name" VARCHAR(150) NOT NULL UNIQUE)
CREATE TABLE "auth_user" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "password" VARCHAR(128) NOT NULL, "last_login" DATETIME NULL, "is_superuser" TINYINT(1) NOT NULL, "username" VARCHAR(150) NOT NULL UNIQUE, "last_name" VARCHAR(150) NOT NULL, "email" VARCHAR(254) NOT NULL, "is_staff" TINYINT(1) NOT NULL, "is_active" TINYINT(1) NOT NULL, "date_joined" DATETIME NOT NULL, "first_name" VARCHAR(150) NOT NULL)
CREATE TABLE "restaurant_booking" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "first_name" VARCHAR(200) NOT NULL, "last_name" VARCHAR(200) NOT NULL, "guest_number" INT NOT NULL, "comment" VARCHAR(1000) NOT NULL)
CREATE TABLE "restaurant_menu" ("id" INT NOT NULL PRIMARY KEY AUTO_INCREMENT, "name" VARCHAR(200) NOT NULL, "price" INT NOT NULL)
CREATE TABLE "django_session" ("session_key" VARCHAR(40) NOT NULL PRIMARY KEY, "session_data" TEXT NOT NULL, "expire_date" DATETIME NOT NULL)